import React, { useRef } from 'react';
import { Toast } from 'primereact/toast';
import { FileUpload } from 'primereact/fileupload';
import { PrimeReactProvider } from 'primereact/api';

export default function Home({setMode}) {
    const toast = useRef(null);

    const onUpload = () => {
        toast.current.show({ severity: 'info', summary: 'Success', detail: 'File Uploaded' });
    };
    return (
        <div className="mainContainer">
            <div className="header">
                <h1>Welcome to ReLevel</h1>
            </div>
            <div className="content">
                <div className='sectionBrowse'>
                    <div className="card flex justify-content-center">
                        <Toast ref={toast}></Toast>
                        <FileUpload mode="basic" name="demo[]" url="/api/upload" accept="image/*" maxFileSize={1000000}
                                    onUpload={onUpload}/>
                    </div>
                </div>

                {/*<div className="api-section">*/}
                {/*    <h2 className='diaryHeading'>Voice Diary</h2>*/}
                {/*    <p className='underText'>Create a voice diary that can transform your entries into songs for a*/}
                {/*        unique and creative experience.</p>*/}
                {/*</div>*/}
            </div>
        </div>
    );
}
