import './App.css';
import Home from './Pages/Home'
import UploadFile from "./Pages/uploadFile";
import Loading from "./Pages/Loading";
import { useState, useEffect } from "react";

function App() {
    const [mode, setMode] = useState(0);
    const [visible, setVisible] = useState(true);

    useEffect(() => {
        setVisible(false); // Start fade out
        const timer = setTimeout(() => {
            setVisible(true); // Reset visibility for the next page
            setMode((prevMode) => (prevMode + 1) % 3); // Update mode, loop back to 0 after 2
        }, 500); // Duration of fade-out animation
        return () => clearTimeout(timer);
    }, [mode]);

    return (
        <div id="Pages" className={visible ? 'visible' : 'hidden'}>
            {
                (() => {
                    switch (mode) {
                        case 0:
                            return <Home setMode={setMode} />;
                        case 1:
                            return <UploadFile setMode={setMode} />;
                        case 2:
                            return <Loading setMode={setMode} />;
                        default:
                            return null; // Handle default case if needed
                    }
                })()
            }
        </div>
    );
}

export default App;
